<?php$sql['count_referensi_mak']   = "SELECT    COUNT(bas.paguBasId) AS `count`FROM `finansi_ref_pagu_bas` AS bas WHERE 	bas.paguBasParentId <> 0 	AND bas.`paguBasId` IN (SELECT rncnpengeluaranMakId     FROM rencana_pengeluaran AS rp    LEFT JOIN kegiatan_detail AS kd     ON kd.`kegdetId` = rp.`rncnpengeluaranKegdetId`     LEFT JOIN kegiatan AS k     ON k.`kegId` = kd.`kegdetKegId`     WHERE k.`kegThanggarId` = (SELECT    `thanggarId`     FROM `tahun_anggaran`    WHERE thanggarIsAktif = 'Y') AND rncnpengeluaranMakId IS NOT NULL) 	AND (bas.paguBasKode LIKE '%s' 	OR bas.paguBasKeterangan LIKE '%s')";$sql['get_ref_mak'] = "SELECT    bas.`paguBasId` AS id,    bas.`paguBasKode` AS kode,    bas.`paguBasParentId` AS parent_id,    bas.`paguBasNilaiDefault` AS nilai_default,    IF(bas.`paguBasStatusAktif` = 'Y', 'Y','T') AS status_aktif,    bas.`paguBasKeterangan` AS nama,     (SELECT paguBasKeterangan     FROM finansi_ref_pagu_bas     WHERE paguBasId = parent_id) AS pagubasname,     bas_tipe.`paguBasTipeId` AS bas_tipe_id,     IFNULL(bas_tipe.`paguBasTipeNama`, '-') AS bas_tipe,     coa.`coaId` AS coa_id,     IFNULL(coa.`coaKodeAkun`, '-') AS coa_kode,     IFNULL(coa.`coaNamaAkun`, '-') AS coa_nama  FROM `finansi_ref_pagu_bas` AS basLEFT JOIN 	finansi_ref_pagu_bas_tipe_bas AS btb ON 	btb.paguBasId = bas.paguBasId LEFT JOIN 	`finansi_ref_pagu_bas_tipe` AS bas_tipe ON 	btb.`paguBasTipeId` = bas_tipe.`paguBasTipeId` LEFT JOIN 	finansi_coa_mak AS cm ON 	cm.`paguBasId` = bas.`paguBasId`LEFT JOIN coa ON 	coa.`coaId` = cm.`coaId`WHERE 	paguBasParentId <> 0 	AND bas.`paguBasId` IN (SELECT rncnpengeluaranMakId     FROM rencana_pengeluaran AS rp    LEFT JOIN kegiatan_detail AS kd     ON kd.`kegdetId` = rp.`rncnpengeluaranKegdetId`     LEFT JOIN kegiatan AS k     ON k.`kegId` = kd.`kegdetKegId`     WHERE k.`kegThanggarId` = (SELECT    `thanggarId`     FROM `tahun_anggaran`    WHERE thanggarIsAktif = 'Y') AND rncnpengeluaranMakId IS NOT NULL)  	AND (bas.paguBasKode LIKE '%s' 	OR bas.paguBasKeterangan LIKE '%s') ORDER BY     paguBasStatusAktif ='Y', paguBasKode ASCLIMIT %s, %s ";?>