<?phpclass AppPopupMak extends Database{    protected $mSqlFile = 'module/movement_anggaran/business/popup_mak.sql.php';        function __construct($connectionNumber=0) {        parent::__construct($connectionNumber);    }        public function CountMak($kode)    {        $sql    = sprintf($this->mSqlQueries['count_referensi_mak'], '%s','%s');        $return = $this->Open($sql, array('%'.$kode.'%','%'.$kode.'%'));        if($return){            return $return[0]['count'];        }else{            echo $this->getLastError();        }    }        public function DataMak($kode,$offset,$limit)    {        $sql    = sprintf($this->mSqlQueries['get_ref_mak'], '%s','%s','%d','%d');               $return = $this->Open($sql, array('%'.$kode.'%','%'.$kode.'%',$offset,$limit));        #if($return){            return $return;        #}else{        #    echo 'Error : '.$this->getLastError();        #}    }}?>